import React, { Component } from "react";
import "../App.css";
import Card from "@material-ui/core/Card";
import IconButton from "@material-ui/core/IconButton";
import TextField from "@material-ui/core/TextField";
import Snackbar from "@material-ui/core/Snackbar";
import Button from "@material-ui/core/Button";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import InputAdornment from "@material-ui/core/InputAdornment";

export default class Reset extends Component{
    render(){
        return(
            <div className="main">
        <div id="card">
          <Card className="content">
            <div>
              <img
                id="logo"
                src={require("/home/admin1/fundoo/Client/src/assets/Fundoo.png")}
                alt="Fundoo"
              />
            </div>
            <br />
            <div>
              <h4>Reset your Password</h4>
              <h6>to continue to Fundoo</h6>
            </div>
            <br />
            <div id="password">
            <div className="reg_textfield4">
                <TextField
                  className="input4"
                  label="Password"
                  type={this.state.showPassword ? "text" : "password"}
                  name="Password"
                  margin="normal"
                  variant="outlined"
                  value={this.state.Password}
                  onChange={this.onChange}
                />
              </div>
              <div className="reg_textfield5">
                <TextField
                  className="input4"
                  label="Confirm"
                  type={this.state.showPassword ? "text" : "password"}
                  name="ConfirmPassword"
                  margin="normal"
                  variant="outlined"
                  value={this.state.ConfirmPassword}
                  onChange={this.onChange}
                />
              </div>
              <div className="reg_textfield6">
                <IconButton
                  id="reg_eye"
                  aria-label="Toggle password visibility"
                  onClick={this.handleClickShowPassword}
                >
                  {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </div>
            </div>
            <br />
            <div id="button">
              <button
                id="button2"
                class="btn btn-primary"
                type="submit"
                onClick={this.handleSubmit}
              >
                {" "}
                Login{" "}
              </button>
              <br />
              <br />
            </div>
            <div>
              <Button id="button3" color="primary" onClick={this.registerclick}>
                Create account
              </Button>
              <br />
            </div>
            <br />
            <br />
          </Card>
        </div>

        <Snackbar
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "right"
          }}
          open={this.state.openSnackBar}
          autoHideDuration={5000}
          onClose={this.handleSnackClose}
          variant="error"
          ContentProps={{
            "aria-describedby": "message-id"
          }}
          message={<span id="message-id"> {this.state.snackBarMessage} </span>}
          action={[
            <div>
              <IconButton
                key="close"
                aria-label="Close"
                color="inherit"
                onClick={this.handleSnackClose}
              />
            </div>
          ]}
        />
      </div>
        );
    }
}
