import React, { Component } from 'react';
import '../App.css';
import { Card, TextField } from '@material-ui/core';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';

export default class Register extends Component{
    constructor(props){
        super(props)
        this.state={
            firstName:"",
            lastName:" ",
            Email:"",
            Password:"",
    };
    }
    render(){
        return(
            <div className="reg_main">
            <div id="reg_card">
            <Card className="reg_content">
            <div><img id="reg_logo" src={require('/home/admin1/fundoo/Client/src/assets/Fundoo.png')
           } alt="Fundoo"></img></div><br/>
            <div><h3>Create your Google Account</h3></div>
            <div id="text-field1">
           <TextField
          id="input1"
          label="First Name"
          type="text"
          name="firstName"
          margin="normal"
          variant="outlined"
          value={""} 
          onChange={""}
        />
        </div>
        <div id="reg_text-field2">
           <TextField
          id="reg_input1"
          label="Last Name"
          type="text"
          name="lastName"
          margin="normal"
          variant="outlined"
          value={""} 
          onChange={""}
        />
        </div>
        <div id="reg_text-field">
           <TextField
          id="reg_input2"
          label="Email"
          type="text"
          name="Email"
          margin="normal"
          variant="outlined"
          value={""} 
          onChange={""}
        />
        </div>
        <div id = "password">
        <TextField
          id="input"
          label="Password"
          name="Password"
          margin="normal"
          variant="outlined"
          type={this.state.showPassword ? 'text' : 'password'}
          value={this.state.password}
          onChange={this.onChange}
          InputProps={{
          endAdornment:(
            <InputAdornment position="end">
              <IconButton
                aria-label="Toggle password visibility"
                onClick={this.handleClickShowPassword}
              >
                {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>
          )
          }}
        />           
           </div><br/>
            </Card>
            </div>
            </div>
        )
    }
}
