import React, { Component } from 'react';
import "../App.css";
import Card from "@material-ui/core/Card";
import Button from "@material-ui/core/Button";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import InputAdornment from "@material-ui/core/InputAdornment";
import IconButton from "@material-ui/core/IconButton";
import TextField from "@material-ui/core/TextField";
import Snackbar from "@material-ui/core/Snackbar";
export default class Reset extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            Password:"",
            ConfirmPassword:"",
            snackBarMessage: "",
            showPassword: false
        }
    }
    render(){
        return (
            <div>
              <div className="reg_main">
                <Card className="reg_content">
                  <div>
                    <img
                      id="reg_logo"
                      src={require("/home/admin1/fundoo/Client/src/assets/Fundoo.png")}
                      alt="Fundoo"
                    />
                  </div>
                  <br />
                  <div id="reg_hed">
                    <h3>Create your Fundoo Account</h3>
                    <h5>to continue to Fundoo</h5>
                  </div>
                  <br />
                  <div className="reg_center">
                    <div className="reg_textfield4">
                      <TextField
                        className="input4"
                        label="Password"
                        type={this.state.showPassword ? "text" : "password"}
                        name="Password"
                        margin="normal"
                        variant="outlined"
                        value={this.state.Password}
                        onChange={this.onChange}
                      />
                    </div>
                    <div className="reg_textfield5">
                      <TextField
                        className="input4"
                        label="Confirm"
                        type={this.state.showPassword ? "text" : "password"}
                        name="ConfirmPassword"
                        margin="normal"
                        variant="outlined"
                        value={this.state.ConfirmPassword}
                        onChange={this.onChange}
                      />
                    </div>
                    <div className="reg_textfield6">
                      <IconButton
                        id="reg_eye"
                        aria-label="Toggle password visibility"
                        onClick={this.handleClickShowPassword}
                      >
                        {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
                      </IconButton>
                    </div>
                    {/* <div id="reg_button1">
                    <Button id="button3" color="primary" >Sign in instead</Button><br/>  </div> */}
                    <div id="reg_button2">
                      <button
                        id="reg_button2"
                        class="btn btn-primary"
                        type="submit"
                        onClick={this.handleSubmit}
                      >
                        {" "}
                        Submit{" "}
                      </button>
                      <br />
                    </div>
                    <div>
                      <Button id="button3" color="primary" onClick={this.loginclick}>
                        Sign in instead
                      </Button>
                      <br /> <br />
                    </div>
                  </div>
                </Card>
              </div>
              <Snackbar
                          anchorOrigin={{
                              vertical: 'top',
                              horizontal: 'right',
                          }}
                          open={this.state.openSnackBar}
                          autoHideDuration={6000}
                          onClose={this.handleSnackClose}
                          variant="error"
                          ContentProps={{
                              'aria-describedby': 'message-id',
                          }}
                          message={<span id="message-id"> {this.state.snackBarMessage} </span>}
                          action={[
                              <IconButton
                                  key="close"
                                  aria-label="Close"
                                  color="inherit"
                                  onClick={this.handleSnackClose}
                              >
                                  {/* <CloseIcon /> */}
                              </IconButton>
                          ]}
      />
            </div>
          );
    }
}
