import React ,{Component} from 'react'
import '../App.css';
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button'
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';
import TextField from '@material-ui/core/TextField';
import Snackbar from '@material-ui/core/Snackbar';
import { userLogin } from '../services/userServices'
export default class Login extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            Email:"",
            Password:"",
            snackBarMessage:"",
            showPassword: false,           
        };

        this.onChange = this.onChange.bind(this);   
        this.handleSubmit=this.handleSubmit.bind(this);
    }

    onChange(e) {
        e.preventDefault()
        this.setState({ [e.target.name]: e.target.value })
    }

    handleClickShowPassword = () => {
        this.setState(state => ({ showPassword: !state.showPassword }));
      };

    handleSnackClose =() =>{
      try {
        this.setState({
          openSnackBar:false
        })
      } catch (err) {
        console.log("snackBar error");

      }

    };
      handleSubmit = event => {
        event.preventDefault();
        if (!this.state.Email) {
            this.setState({
                openSnackBar: true,
                snackBarMessage: "email cannot be empty..!"
            });
        } else if (!this.state.Password) {
            this.setState({
                openSnackBar: true,
                snackBarMessage: "Password cannot be empty..!"
            });
        } else if (
            !/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$/.test(this.state.Email)
        ) {
            this.setState({
                openSnackBar: true,
                snackBarMessage: "Invalid Email..!"
            });
        } else if (this.state.Password.length < 6) {
            this.setState({
                openSnackBar: true,
                snackBarMessage: "Password must be of atleast 6 characters long..!"
            });
        } else {
            var data = {
                Email: this.state.Email,
                Password: this.state.Password
            }
            userLogin(data)
                .then((response) => {
                    console.log(response);
                    this.setState({
                        openSnackBar: true,
                        snackBarMessage: "Login Successfull!!"
                    });
                    localStorage.setItem('Sender', this.state.Email);
                    this.props.history.push('/dashBoard');
                })
                .catch((err) => {
                    console.log(err);
                    this.setState({
                        openSnackBar: true,
                        snackBarMessage: "Login failed!!"
                    });
                });
        }
    }


    render(){

        return(
            <div className="main">
           <div id="card">
           <Card className="content">
            <div><img id="logo" src={require('/home/admin1/fundoo/Client/src/assets/Fundoo.png')
           } alt="Fundoo"></img></div><br/>
           <div><h4>Sign in</h4>
           <h6>to continue to Fundoo</h6></div><br/>
           <div id="text-field">
           <TextField
          id="input1"
          label="Email"
          type="text"
          name="Email"
          margin="normal"
          variant="outlined"
          value={this.state.Email} 
          onChange={this.onChange}
        />
        </div>

        <div id = "password">
        <TextField
          id="input"
          label="Password"
          name="Password"
          margin="normal"
          variant="outlined"
          type={this.state.showPassword ? 'text' : 'password'}
          value={this.state.password}
          onChange={this.onChange}
          InputProps={{
          endAdornment:(
            <InputAdornment position="end">
              <IconButton
                aria-label="Toggle password visibility"
                onClick={this.handleClickShowPassword}
              >
                {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>
          )
          }}
        />           
           </div><br/>
           <div id="button">
           <Button id="button1" color="primary" onClick={this.handleSubmit}>Forgot password?</Button>
           <button id="button2" class="btn btn-primary" type="submit" onClick={this.handleSubmit}> Login </button><br/><br/>
           </div>
           <div>
           <Button id="button3" color="primary" >Create account</Button><br/>         
           </div><br/><br/>
           </Card>
           </div>

           <Snackbar
                    anchorOrigin={{
                        vertical: 'bottom',
                        horizontal: 'right',
                    }}
                    open={this.state.openSnackBar}
                    autoHideDuration={5000}
                    onClose={this.handleSnackClose}
                    variant="error"
                    ContentProps={{
                        'aria-describedby': 'message-id',
                    }}
                    message={<span id="message-id"> {this.state.snackBarMessage} </span>}
                    action={[
                      <div>
                        <IconButton 
                        key="close"
                        aria-label="Close"
                        color="inherit"
                        onClick={this.handleSnackClose}
                        />
                        </div>


                    ]}
                />
            </div>
        );
    }
}
