import React ,{Component} from 'react'
import '../App.css';
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button'
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';
import TextField from '@material-ui/core/TextField';
export default class Login extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            Email:"",
            Password:"",
            showPassword: false,           
        };
        this.onChange = this.onChange.bind(this);   
        // this.handleSubmit = this.handleSubmit.bind(this);
    }
    onChange(e) {
        e.preventDefault()
        this.setState({ [e.target.name]: e.target.value })
    }

    handleClickShowPassword = () => {
        this.setState(state => ({ showPassword: !state.showPassword }));
      };

    // hhandleSubmit = event => {
    //     event.preventDefault();
    //     if (!/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$/.test(this.state.Username)) {}
    // }
    render(){

        return(
            <div className="main">
           <div id="card">
           <Card className="content">
            <div><img id="logo" src={require('/home/admin1/fundoo/Client/src/assets/Fundoo.png')
           } alt="Fundoo"></img></div><br/>
           <div><h4>Sign in</h4>
           <h6>to continue to Fundoo</h6></div><br/>
           <div id="text-field">
           <TextField
          id="input1"
          label="Email"
          type="text"
          name="Email"
          margin="normal"
          variant="outlined"
          value={this.state.Email} 
          onChange={this.onChange}
        /></div><div>
        <TextField
          id="input"
          label="Password"
          name="Password"
          margin="normal"
          variant="outlined"
          type={this.state.showPassword ? 'text' : 'password'}
          value={this.state.password}
          onChange={this.onChange}
          InputProps={{
          endAdornment:(
            <InputAdornment position="end">
              <IconButton
                aria-label="Toggle password visibility"
                onClick={this.handleClickShowPassword}
              >
                {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>
          )
          }}
        />

           </div><br/>
           <div id="button">
           <Button id="button1" color="primary" onClick={this.handleSubmit}>Forgot password?</Button>
           <button id="button2" class="btn btn-primary" type="submit"> Login </button><br/><br/>
           </div>
           <div>
           <Button id="button3" color="primary" >Create account</Button><br/>         
           </div><br/><br/>
           </Card>
           </div>
            </div>
        );
    }
}
