import React, { Component } from "react";
import "../App.css";
import { Card, TextField,Button } from "@material-ui/core";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import IconButton from "@material-ui/core/IconButton";

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      Email: "",
      Password: ""
    };
    this.onChange = this.onChange.bind(this); 


  }
  onChange(e) {
    e.preventDefault()
    this.setState({ [e.target.name]: e.target.value })
}

  handleClickShowPassword = () => {
    try {
        this.setState(state => ({ showPassword: !state.showPassword }));
    } catch (err) {
        console.log("error at handleClickShowPassword in registration");
    }
};
  render() {
    return (
      <div>
          <div className="reg_main">
            <Card className="reg_content">
            <div><img id="logo" src={require('/home/admin1/fundoo/Client/src/assets/Fundoo.png')
           } alt="Fundoo"></img></div><br/>
           <div id="reg_hed"><h5>Create your Fundoo Account</h5>
           <h6>to continue to Fundoo</h6></div><br/>
           <div className="reg_center">
              <div className="reg_textfield1">
                <TextField
                  className="input1"
                  label="First Name"
                  type="text"
                  name="firstName"
                  margin="normal"
                  variant="outlined"
                  value={this.state.firstName}
                  onChange={this.onChange}
                />
                <TextField
                  className="input1"
                  label="Last Name"
                  type="text"
                  name="lastName"
                  margin="normal"
                  variant="outlined"
                  value={this.state.lastName}
                  onChange={this.onChange}
                />

              </div>
              <div className="reg_textfield2">
                <TextField
                  className="input3"
                  label="Email"
                  type="text"
                  name="Email"
                  margin="normal"
                  variant="outlined"
                  value={this.state.Email}
                  onChange={this.onChange}
                />
              </div>
              <div>
                <TextField
                  className="input1"
                  label="Password"
                  type={this.state.showPassword ? 'text' : 'password'}
                  name="Password"
                  margin="normal"
                  variant="outlined"
                  value={this.state.Password}
                  onChange={this.onChange}
                />
                <TextField
                  className="input2"
                  label="Confirm"
                  type={this.state.showPassword ? 'text' : 'password'}
                  name="ConfirmPassword"
                  margin="normal"
                  variant="outlined"
                  value={this.state.ConfirmPassword}
                  onChange={this.onChange}
                />
                <IconButton
                  aria-label="Toggle password visibility"
                  onClick={this.handleClickShowPassword}
                >
                  {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </div>

              <div id="reg_button">
           <button id="reg_button2" class="btn btn-primary" type="submit" onClick={this.handleSubmit}> Submit </button><br/><br/>
           </div>
           <div>
           <Button id="button3" color="primary" >Sign in instead</Button><br/>         
           </div><br/><br/>
           </div>
            </Card>
          </div>        
      </div>
    );
  }
}
